/*Table structure for table `message` */

DROP TABLE IF EXISTS `message`;

CREATE TABLE `message` (
  `id` int(11) NOT NULL AUTO_INCREMENT,
  `from_id` int(11) DEFAULT NULL COMMENT '1是系统用户，系统发的消息是通知，不是私信。其他用户发的消息是私信',
  `to_id` int(11) DEFAULT NULL,
  `conversation_id` varchar(45) NOT NULL COMMENT '私信时，这里存的xxx_yyy,是通信两方的userId，不管谁给谁发消息，永远有xxx<yyy。通知中，这里存的是消息类型：关注/评论/点赞',
  `content` text COMMENT '通知中，这里存的是Event对象的JSON字符串。comment、like存的entityType、entityId、postId、userId。follow存的entityType、entityId、userId。',
  `status` int(11) DEFAULT NULL COMMENT '0-未读;1-已读;2-删除;',
  `create_time` timestamp NULL DEFAULT NULL,
  PRIMARY KEY (`id`),
  KEY `index_from_id` (`from_id`),
  KEY `index_to_id` (`to_id`),
  KEY `index_conversation_id` (`conversation_id`)
) ENGINE=InnoDB AUTO_INCREMENT=425 DEFAULT CHARSET=utf8;

